$(document).ready((function(){$(".loader").hide();const e=e=>{e.map(e=>{if(e.multimedia.length>0){const t=`<div class="article-container"><img src="${e.multimedia[4].url}" class="article-image" alt="" /><p class="article-desc">${e.abstract}</p></div>`;$(".top-stories").append(t)}$(".loader").hide()})};$("#myList").on("change",()=>{if($(".top-stories").html(""),$(".loader").show(),""===$("option:selected").val())return;(t=>{$.ajax({url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=uVsGON6UfLMCDT3ebpRGQZsw0MSqmPCU`,type:"GET",success:function(t){t.results;e(t.results)}})})($("option:selected").text().toLowerCase())})}));